import React, {Component} from 'react';
import userPhoto from './img/userX.PNG';
import { FontAwesomeIcon } from '@fortawesome/react-fontawesome';
import { faPen, faMapMarker, faCertificate} from '@fortawesome/free-solid-svg-icons';

function UserProfile () {
    return (
        <div>
            Perfil de usuario<br/><br/>

            <div>

                        <div>
                         <div style = {row}>
                            <button style = {botonEdita}>
                                <div style = {row}>
                                    <FontAwesomeIcon icon={faPen} />  &nbsp; &nbsp;
                                    <div style = {edita}> Edita </div>
                                 </div>
                            </button>
                         </div>
                         <div style = {row}>
                            <div style = {column1}>
                                <img src={userPhoto} style = {widthMax}></img><br/><br/>
                                <div style = {localitzacio}><FontAwesomeIcon icon={faMapMarker} />  &nbsp; Localització</div> <br/>
                                <div style = {localitzacio}><FontAwesomeIcon icon={faCertificate} />  &nbsp; 400 xapes</div>

                            </div>
                            <div style = {column2}>
                                <div style = {title} >Nom Usuari</div>
                                <div>Carles Puigdemont</div><br/>
                                <div style = {title} >Correu Electrònic</div>
                                <div>puchiLoParteNoseque@protonmail.com</div><br/>
                                <div style = {title} >Sobre mi</div><br/>
                            </div>
                          </div>
                    </div>
                    </div>
        </div>
    );
}
const row = {
      display: "flex"
};

const column1 = {
    float: "left",
    width: "20%",
    padding: "10px",
    alignItem: "center"

};
const column2 = {
    float: "left",
    width: "80%",
    padding: "10px"
};
const edita = {
    fontSize: 22
};
const localitzacio = {
    fontSize: 22,
    justifyContent: "center",
    display: "flex"
};
const botonEdita = {
    padding: "7px 15px 0px 10px",
    marginRight: "15%",
    marginLeft:"auto"
};
const widthMax = {
        width: "90%",
        height: "auto",
        borderRadius: "50%"
};

const title = {
    fontSize: 22,
    color: "lightgrey"
};

export default UserProfile;
