import React from 'react';
import userPhoto from './img/userX.PNG';

function Experiencies({experiencia, handleInscription}) {

    const {id, title, body, image, owner, xapes, data} = experiencia;

    const inscription = () => {
        handleInscription(id);
    }

    return (
        <div style = {row}>
            <div style = {column1}>
                <img src={userPhoto} style = {widthMax}></img>
                {image}
            </div>

            <div style = {column2}>
                <div style = {row}>
                    <div style = {column2}>
                    <h1 style={mystyle}>{title}</h1>
                    <span style= {description}>{body}</span> <br/>
                     <span style= {nameUser}>{owner}</span> <br/>
                    </div>
                    <div style = {column1}>
                        {xapes} <br/>
                        {data} <br/>
                    </div>
                    {}
                </div>
            </div>
            { /*<!-- Divider -->*/}
            <hr className="sidebar-divider d-none d-md-block"/>
        </div>
    );
}

const mystyle = {
    color: "orange",
    padding: "5px",
    fontSize: 25
};

const row = {
      display: "flex"
};

const widthMax = {
      maxWidth: "90%"
};

const column1 = {
    float: "left",
    width: "20%",
    padding: "10px"
};
const column2 = {
    float: "left",
    width: "80%",
    padding: "10px"
};
const description = {
    fontSize: 22
};
const nameUser = {
    fontSize: 20,
    fontType: "bold"
};

export default Experiencies;
