import React, {useState} from 'react';
import userPhoto from './img/userX.PNG';
import { FontAwesomeIcon } from '@fortawesome/react-fontawesome';
import { faPen, faMapMarker, faCertificate} from '@fortawesome/free-solid-svg-icons';

function UserProfile () {

    const [showButton, setshowButton] = useState("");
    const [voluntariados, setVoluntariados] = useState([
        {id:1, title: "Limpiemos las playas", body: "Necesitan ayuda para limipiar la Barceloneta", image: null, owner: "Clara"},
        {id:2, title: "Ayuda para sintechos", body: "Muchas personas sin casa por Girona, ven a colaborar", image: null, owner: "Eustaquio"},

    ]);

    const [experiencia, setExperiencies] = useState([
        {id:1, title: "Festival Cruïlla", body: "Pots bescanviar les teves xapes per una entrada al Cruïlla!", image: null, owner: "Damm", xapes: "400", data : "20/8/2021", ciutat: "Barcelona"},
        {id:2, title: " \"Carlos de Pablo i el món\" al MACBA ", body: "Carlos de Pablo ens mostrarà la seva forma de veure el món amb la seva exposició al MACBA.", image: null, owner: "MACBA", xapes: "200",  data :"10/10/2021", ciutat: "Barcelona"},
        {id:3, title: " Concert virtual Michael Jackson", body: "Reviu un espectacle del rei del pop de manera online i exclusiva.", image: null, owner: "Tickets Ramon", xapes: "400",  data :"1/23/2022" , ciutat: "Online"}

    ]);

    return (
        <div>
            Perfil de usuario<br/><br/>

            <div>
                <div>
                     <div style = {row}>
                        <button style = {botonEdita} onClick  = {(event) => {setshowButton(event.target.value);} }>
                            <div style = {row}>
                                <FontAwesomeIcon icon={faPen} />  &nbsp; &nbsp;
                                <div style = {edita}> Edita </div>
                             </div>
                        </button>
                     </div>
                     <div style = {row}>
                        <div style = {column1}>

                            <div style = {localitzacio}> <img src={userPhoto} style = {widthMax}></img></div><br/>
                            <div style = {localitzacio}><FontAwesomeIcon icon={faMapMarker} />  &nbsp; Localització</div> <br/>
                            <div style = {localitzacio}><FontAwesomeIcon icon={faCertificate} />  &nbsp; 400 xapes</div>

                        </div>

                        <div style = {column2}>
                            <br/>
                                <div style = {title} >Nom Usuari</div>
                                <div style={{visibility: this.state.showButton ? 'visible' : 'hidden' }}>Carles Puigdemont</div><br/>
                                <div style = {title} >Correu Electrònic</div>
                                <div>PuchiLoParteNoseque@protonmail.com</div><br/>
                                <div style = {title} >Sobre mi</div>
                                <div>Ex presi de la gene i em van fotre fora ara estic a Bruseles fent voluntariats molt guais!</div><br/>
                            </div>
                        </div>
                        { /*<!-- Divider -->*/}
                        <hr className="sidebar-divider d-none d-md-block"/>
                        <ul style = {scroll}>
                            <div style = {column}>

                              <div style = {bold}>Voluntariats fets</div><br/>
                              {voluntariados.map((volutariado) => { return(<li style={subtitle}>  {volutariado.title} </li> ); })}




                            </div>
                            <div style = {column}>

                              <div style = {bold}>Experiències Bescanviades</div><br/>
                              {experiencia.map((volutariado) => { return(<li style={subtitle}>  {volutariado.title} </li> ); })}

                            </div>
                        </ul>
                    </div>
                </div>
        </div>
    );
}

const dividervert = {
    width: 0,
	borderRight: "1px solid #e3e6f0",
	height: "calc(4.375rem - 2rem)",
	margin:" auto 1rem"
	};
const row = {
      display: "flex"
};

const column1 = {
    float: "left",
    width: "20%",
    padding: "10px",
    alignItem: "center"

};
const column2 = {
    float: "left",
    width: "80%",
    padding: "10px"
};
const column = {
    float: "left",
    width: "50%",
    padding: "10px"
};
const edita = {
    fontSize: 22
};
const localitzacio = {
    fontSize: 22,
    justifyContent: "center",
    display: "flex"
};

const botonEdita = {
    padding: "7px 15px 0px 10px",
    marginRight: "15%",
    marginLeft:"auto"
};
const widthMax = {
        width: "70%",
        height: "auto",
        borderRadius: "50%",
        justifyContent: "center",
        display: "flex"
};
const bold = {
    fontWeight: "bold"
};

const title = {
    fontSize: 22,
    color: "lightgrey"
};
const subtitle = {
    fontSize: 22,
    paddingLeft: "1%"
};
const scroll = {
    overflowY: "scroll",
    maxHeight: "20vh"
};
export default UserProfile;
